/*Table structure for table `rk_uploads` */

DROP TABLE IF EXISTS `rk_uploads`;

CREATE TABLE `rk_uploads` (
  `aid` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `arcid` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '所属文章或产品id',
  `title` char(60) NOT NULL DEFAULT '' COMMENT '图片描述',
  `src` char(80) NOT NULL DEFAULT '' COMMENT '图片地址',
  `newname` varchar(100) DEFAULT NULL COMMENT '新的文件名',
  `filename` varchar(100) NOT NULL DEFAULT '1' COMMENT '原文件名',
  `width` char(10) NOT NULL DEFAULT '',
  `height` char(10) NOT NULL DEFAULT '',
  `filesize` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `uptime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '入库时间',
  PRIMARY KEY (`aid`),
  KEY `arcid` (`arcid`)
) ENGINE=MyISAM AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;

